const inputs = document.querySelectorAll(".form-item__input")

for (let index = 0; index < inputs.length; index++) {
  const input = inputs[index];
  const form_label = document.querySelectorAll(".form-item__label")[index];

  input.addEventListener("focusin", function (event) {
    form_label.classList.add("active")
  });
  input.addEventListener("focusout", function (event) {
    if (!input.value) {
      form_label.classList.remove("active")
    }
  });
}


// dropdown menu
const dropdownBtns = document.querySelectorAll(".qq__dropdown-btn")
const dropdowns = document.querySelectorAll(".qq__dropdown")

for (let index = 0; index < dropdownBtns.length; index++) {
  const dropdownBtn = dropdownBtns[index];
  const dropdown = dropdowns[index];

  dropdownBtn.addEventListener("click", function () {
    if (dropdown.classList.contains("closed")) {
      dropdown.classList.remove("closed")
      dropdown.classList.add("open")
    } else {
      dropdown.classList.add("closed")
      dropdown.classList.remove("open")
    }
    this.classList.toggle("active")

  })
}


// language
var langs = document.querySelectorAll(".language__select")
for (var i = 0; i < langs.length; i++) {
  const lang = langs[i]
  lang.addEventListener("change", function () {
    const langValue = this.value
    const currentPage = window.location.pathname.match(/\w*.html/)[0]
    let href = ""
    this.selectedIndex = 0
    if (langValue) {
      href = location.origin + window.location.pathname.replace(currentPage, langValue + currentPage)
      location.href = href

    } else {
      href = location.origin + window.location.pathname.replace("en/", "")
      location.href = href
    }
  })
}
